<script setup>
import { ref, computed } from 'vue'

const whiteimg = 'data:image/png;base64,';
const blackimg = 'data:image/png;base64,';
  const logo = ref('white');
  const color = ref('white')
const name = ref('开往');
const style = ref('for-the-badge');
const url = ref('https://www.travellings.cn/go.html');

// 使用计算属性实时生成图片URL
const imgurl = computed(() => {
  const params = new URLSearchParams();
  
  // 根据选择的logo类型添加参数
  if (logo.value === "white") {
    params.append("logo", whiteimg);
  } else if (logo.value === "black") {
    params.append("logo", blackimg);
  }
  
  params.append("link", url.value);
  params.append("style", style.value);
  
  return `https://img.shields.io/badge/${encodeURIComponent(name.value)}-${color.value}?${params.toString()}`;
});
</script>

<template>
  <ClientOnly>
  <div>
    这里选择您想要的开往LOGO样式，或者不要LOGO。
  </div>
  <select v-model="logo" class="form-control">
    <option value="white">白色方形</option>
    <option value="black">黑色方形</option>
    <option value="nologo">无LOGO</option>
  </select>
  <div>
    这里选择Badge的填充颜色。
  </div>
  <select v-model="color" class="form-control">
    <option value="white">白色</option>
    <option value="black">黑色</option>
    <option value="blue">蓝色</option>
  </select>
  <div>
    这里选择您想要的开往名称。
  </div>
  <select v-model="name" class="form-control">
    <option value="Travellings">英文名称（Travellings）</option>
    <option value="开往">中文名称（开往）</option>
  </select>
  <div>
    这里选择您想要的Badge样式。
  </div>
  <select v-model="style" class="form-control">
    <option>flat</option>
    <option>for-the-badge</option>
  </select>
  <div>
    这里填写您想要的开往跳转页面链接。
  </div>
  <input v-model="url" class="form-control">
  <p>
   您的Badges链接为 {{imgurl}} 。样例如下：
  </p>
  <img :src="imgurl">
    </ClientOnly>
</template>

