import{_ as t,c as o,o as s,j as e,a as n}from"./chunks/framework.Ja7AKUN6.js";const b=JSON.parse('{"title":"开往维护组关于停止维护QQ群的公告","description":"","frontmatter":{"sidebar_position":4,"title":"开往维护组关于停止维护QQ群的公告","date":"2023-09-03T00:00:00.000Z","updated":"2023-09-03T00:00:00.000Z","categories":"项目公告","tags":["提议公开"]},"headers":[],"relativePath":"announcements/qq-group-closed.md","filePath":"announcements/qq-group-closed.md"}'),a={name:"announcements/qq-group-closed.md"},c=e("h1",{id:"开往维护组关于停止维护-qq-群的公告",tabindex:"-1"},[n("开往维护组关于停止维护 QQ 群的公告 "),e("a",{class:"header-anchor",href:"#开往维护组关于停止维护-qq-群的公告","aria-label":'Permalink to "开往维护组关于停止维护 QQ 群的公告"'},"​")],-1),r=e("p",null,"开往维护组成员于 2023 年 8 月 30 日 提议取消维护 QQ 群并发布了组内投票。截止至 2023 年 9 月 2 日，共有 5 人参与了投票，其中投赞成票 4 人，投弃权票 1 人，符合《开往运维规定》中对 B 类事项的组内投票要求，开往停止维护 QQ 群的提议通过。 自本公告公布之日起，开往放弃对 QQ 群（群号：282861667）的管理、删除项目说明文件中 QQ 群的有关内容并通知敦促 QQ 群负责人及时解散群聊，不再通过该群发布开往项目有关通知，不再接收来自该群的针对项目的反馈和建议。 在停止维护 QQ 群后，开往成员可以通过 GitHub 仓库、成员联系方式、首页留言区、项目博客等方式与开往维护组取得联系。",-1),d=e("p",null,"开往维护组 2023 年 9 月 3 日",-1),i=e("p",null,"附：有关开往组内提议的内容 事由： 目前官方渠道过多，不够聚焦。项目早期应当尽可能把信息汇总在一处。QQ 群经过长时间的发展群内有大量开往与非开往成员并存。",-1),l=e("p",null,"方案： 解散 QQ 群",-1),_=e("p",null,"利： 1.项目所有信息在 git 与开往博客上申请、审核、公示、讨论。项目更聚焦，也更能吸引有水平的博主。 2.减少维护的渠道，维护工作更专注于审核、优化设计本身。 3.为后续站点一体化（论坛/留言板/博客）铺垫。 弊： 1.当用户无法使用 git 粘性下降。",-1),Q=[c,r,d,i,l,_];function p(u,h,m,q,f,g){return s(),o("div",null,Q)}const B=t(a,[["render",p]]);export{b as __pageData,B as default};
