import{_ as t,c as a,o as e,a2 as i}from"./chunks/framework.Ja7AKUN6.js";const b=JSON.parse('{"title":"审查制度","description":"","frontmatter":{"sidebar_position":2,"title":"审查制度","date":"2025-05-02T00:00:00.000Z","updated":"2025-05-02T00:00:00.000Z","categories":"项目公告","tags":["审查制度"]},"headers":[],"relativePath":"wiki/check.md","filePath":"wiki/check.md"}'),r={name:"wiki/check.md"},o=i('<h1 id="审查制度" tabindex="-1">审查制度 <a class="header-anchor" href="#审查制度" aria-label="Permalink to &quot;审查制度&quot;">​</a></h1><h2 id="总体流程" tabindex="-1">总体流程 <a class="header-anchor" href="#总体流程" aria-label="Permalink to &quot;总体流程&quot;">​</a></h2><p>下图为开往的审核制度图，开往的审核分为：「首次审核」与「复核」。</p><p><img src="https://www.travellings.cn/assets/img/checkmap.png" alt="img"></p><h2 id="首次审核" tabindex="-1">首次审核 <a class="header-anchor" href="#首次审核" aria-label="Permalink to &quot;首次审核&quot;">​</a></h2><p>注意：一般情况下，巡查组无需负责，了解即可！</p><p>首次审核是指网站初次申请加入项目时，用户在GitHub上提交issue的过程。通常情况下，这一审核环节主要由维护组负责处理，巡查组则处于了解状态，无需直接介入。然而，在特定情况下，根据通知安排，巡查组也可能承担起首次审核的职责。</p><p>审核流程概述</p><ol><li>提交与接收：用户通过GitHub提交加入项目的申请issue后，维护组会收到这些申请。</li><li>审核与标记：维护组成员根据既定的加入要求，对每份申请进行详细审核。若网站符合标准，则会在issue上标注“审核通过”的标签（Labels），并触发机器人自动将该网站推送至成员列表以供进一步确认。对于未通过审核的网站，则会标注“审核驳回”的标签，并附上不通过的具体理由，等待站长根据反馈进行调整。</li></ol><h2 id="复核机制" tabindex="-1">复核机制 <a class="header-anchor" href="#复核机制" aria-label="Permalink to &quot;复核机制&quot;">​</a></h2><p>复核环节主要由巡查组负责。在项目运行过程中，若已加入的站点因某种原因未能持续满足项目要求，巡查机器人会自动在成员列表中为该站点打上相应的tag，并附上ID号。此时，巡查组将依据这些标签与ID号，重新开启对应的issue，向站长提出具体的改进要求或指出存在的问题，以确保所有站点均符合项目的既定标准。</p><table tabindex="0"><thead><tr><th>成员列表Tag说明</th><th></th></tr></thead><tbody><tr><td>图标</td><td>含义</td></tr><tr><td>X</td><td>网站无法访问</td></tr><tr><td>!</td><td>未正确/没有悬挂开往链接</td></tr></tbody></table><blockquote><p>注意：巡查机器人的数据不一定正确</p></blockquote>',13),s=[o];function d(c,l,h,n,p,_){return e(),a("div",null,s)}const m=t(r,[["render",d]]);export{b as __pageData,m as default};
